mapper.select:
	select m.mem_name, B.*
	from member m,
	(select p.mem_no mem, A.* from pilot p,
	(select pil_no, pil_title, con_no, con_sort, pl.list_no, c.mem_no, con_time,
	con_attend, con_purpose, con_loc, to_char(con_start,'yyyy/MM/dd')
	con_start_str, to_char(con_end,'yyyy/MM/dd') con_end_str,
	to_char(con_regdate,'yyyy/MM/dd') con_regDate_str
	from pil_list pl, condition c
	where pl.list_no = c.list_no and mem_no = #{mem_no}

		and c.con_no = #{con_no}

   ) A where p.pil_no = A.pil_no) B where m.mem_no = B.mem

	select m.mem_name,p.*, pl.* from pil_list pl, member m, pilot p 
    where p.pil_no = pl.pil_no and p.mem_no = m.mem_no and 
    pl.list_no = #{info}

   select A.* from (select rownum n, m.mem_name, pl.* from pil_list pl, member m, pilot p 
    where p.pil_no = pl.pil_no and p.mem_no = m.mem_no



		and pil_locInfo like '%' || #{location} || '%'



		and pil_cateInfo = #{category}



    	and pil_cateInfo = #{category} and  pil_locInfo like '%' || #{location} || '%' 



    	and pl.pil_no in (select pil_no from possesion where pil_no is not null)

     ) A where A.n between #{start} and #{end}
